if(!origin){
    origin = this.actor.getActiveTokens();
}
if(args.context.purge){
    console.log
    new Sequence()
        .effect()
            .file("jb2a.energy_attack.01.blue")
            .atLocation(origin[0])
	        .scale(.5)
        .effect()
            .file("jb2a.impact_themed.ice_shard.01.blue")
            .atLocation(origin[0])
        .sound()
            .file("assets/AUDIO/purge.mp3")
            .volume(.9)
        .play()
}

if (args.opposed && args.opposed.attackerTest.item?.system.isRanged)
                            {
                                args.modifiers.push({value : -6, label : this.effect.name, armour : true})
                            }
